insert  into `archetypes`(`id`,`name`,`description`) values 
(1,'Arcanista','Los arcanistas son aquellos personajes que han dedicado su vida al estudio de la magia y de lo arcano.'),
(2,'Erudito','Los eruditos son aquellos personajes que han dedicado su vida a la recopilación de conocimientos sobre lo mundano.'),
(3,'Religioso','Los religiosos son aquellos que han dedicado su existencia al estudio de lo divino.'),
(4,'Fanático','Los fanáticos son fieles devotos que llevan la voluntad de su patrón allí donde sea requerida.'),
(5,'Pendenciero','Las peleas de taberna y las broncas callejeras son la salsa donde se cuece este tipo de personajes.'),
(6,'Pillo','Los pillos pasan sus días preocupándose de sus problemas e intentando solucionarlos con el esfuerzo de los demás.'),
(7,'Ermitaño','Los ermitaños viven aislados de la sociedad, siguiendo sus propias leyes.'),
(8,'Herrero','Los herreros pueden dar forma al metal, el cuero y la madera para crear objetos y utensilios de todo tipo.'),
(9,'Alquimista','Los alquimistas, herbolarios y apotecarios pueden, a golpe de mortero y alambique, crear poderosas pociones y ungüentos que poco tienen que envidiar a la más refinada magia.'),
(10,'Productor','El personaje suministra o produce algún tipo de recurso valioso.'),
(11,'Comerciante','El personaje se gana la vida con la compra/venta de bienes y servicios.'),
(12,'Burgués','El personaje es un miembro bienestante de la sociedad y posee amplios recursos a su disposición.'),
(13,'Caballero','El personaje sigue un estricto código de honor y hará de proteger al débil su misión.'),
(14,'Pacifista','Los pacifistas abogan por la paz y la vida, renegando de las armas y la violencia.'),
(15,'Interrogador','El personaje puede utilizar la tortura y el dolor físico para extraer de su víctima la información que requiera.');
